mapper.insert:
insert into entity_cookie (id, c_key, domain,
  expire_time, case_id, create_time,
  update_time, cas_login_id, c_value
  )
values (#{id,jdbcType=INTEGER}, #{cKey,jdbcType=VARCHAR}, #{domain,jdbcType=VARCHAR},
  #{expireTime,jdbcType=TIMESTAMP}, #{caseId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP},
  #{updateTime,jdbcType=TIMESTAMP}, #{casLoginId,jdbcType=INTEGER}, #{cValue,jdbcType=LONGVARCHAR}
  )

update.foreach:
update entity_cookie
c_key = #{item.cKey,jdbcType=VARCHAR}, c_value = #{item.cValue,jdbcType=LONGVARCHAR}, domain = #{item.domain,jdbcType=VARCHAR}, expire_time = #{item.expireTime,jdbcType=TIMESTAMP}, case_id = #{item.caseId,jdbcType=INTEGER}, update_time=#{item.updateTime,jdbcType=TIMESTAMP}, create_time=#{item.createTime,jdbcType=TIMESTAMP}
WHERE id = #{item.id}
